graphql.query:
query Query {
  buscarTransacaoPeloId(id: "31") {
    id
    cartao
    descricao {
      codigoAutorizacao
      dataHora
      estabelecimento
      nsu
      status
      valor
    }
    formaPagamento {
      parcelas
      tipo
    }
  }
}

query Query {
  listarTransacoes {
    id
    cartao
    descricao {
      codigoAutorizacao
      dataHora
      estabelecimento
      nsu
      status
      valor
    }
    formaPagamento {
      parcelas
      tipo
    }
  }
}

mutation($transacao: TransacaoRequestDto){
  pagar(
    transacao: $transacao
  ) {
    id
    cartao
    descricao {
      codigoAutorizacao
      dataHora
      estabelecimento
      nsu
      status
      valor
    }
    formaPagamento {
      tipo
      parcelas
    }
  }
}

mutation Mutation {
  estornar(id: "34") {
    id
    formaPagamento {
      parcelas
      tipo
    }
    descricao {
      codigoAutorizacao
      dataHora
      estabelecimento
      nsu
      status
      valor
    }
    cartao
  }
}
